// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

contract PythStructs {
    // A price with a degree of uncertainty, represented as a price +- a confidence interval.
    //
    // The confidence interval roughly corresponds to the standard error of a normal distribution.
    // Both the price and confidence are stored in a fixed-point numeric representation,
    // `x * (10^expo)`, where `expo` is the exponent.
    //
    // Please refer to the documentation at https://docs.pyth.network/documentation/pythnet-price-feeds/best-practices for how
    // to how this price safely.
    struct Price {
        // Price
        int64 price;
        // Confidence interval around the price
        uint64 conf;
        // Price exponent
        int32 expo;
        // Unix timestamp describing when the price was published
        uint publishTime;
    }

    // PriceFeed represents a current aggregate price from pyth publisher feeds.
    struct PriceFeed {
        // The price ID.
        bytes32 id;
        // Latest available price
        Price price;
        // Latest available exponentially-weighted moving average price
        Price emaPrice;
    }
}

contracts/ChronicleDataProvider.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.16;

import "./IChronicle.sol";
import "./IDataProvider.sol";
import { IERC20 } from "./IERC20.sol";

interface ISelfKisser {
	/// @notice Kisses caller on oracle `oracle`.
	function selfKiss(address oracle) external;
}

contract ChronicleDataProvider is IDataProvider {
	/// @notice The token to read data for.
	address public token;
	/// @notice The Chronicle oracle to read from.
	IChronicle public chronicle =
		IChronicle(address(0xdd6D76262Fd7BdDe428dcfCd94386EbAe0151603));
	/// @notice The chain ID of the network.
	uint32 public chainId;

	/// @notice The SelfKisser granting access to Chronicle oracles.
	ISelfKisser public selfKisser =
		ISelfKisser(address(0x0Dcc19657007713483A5cA76e6A7bbe5f56EA37d));

	constructor(address _token, uint32 _chainId) {
		// Note to add address(this) to chronicle oracle's whitelist.
		// This allows the contract to read from the chronicle oracle.
		selfKisser.selfKiss(address(chronicle));
		chainId = _chainId;
		token = _token;
	}

	/// @notice Function to read the latest data from the Chronicle oracle.
	/// @return val The current value returned by the oracle.
	/// @return age The timestamp of the last update from the oracle.
	function read() external view returns (uint256 val, uint256 age) {
		(val, age) = chronicle.readWithAge();
	}

	function getLabel() external view override returns (string memory) {
		return string(abi.encodePacked("priceChronicle", IERC20(token).name()));
	}

	function getMetricData(
	) external view override returns (uint256) {
		(uint256 val, ) = chronicle.readWithAge();
		return val;
	}

	function getDataTimestamp() external view override returns (uint256) {
		(, uint256 age) = chronicle.readWithAge();
		return age;
	}

	function getTags() external view override returns (string[] memory) {
		string[] memory tags = new string[](4);
		tags[0] = "partner";
		tags[1] = "chronicle";
		tags[2] = "price";
		tags[3] = IERC20(token).name();
	}

	function getDataType() external pure returns (DataTypes) {
		return DataTypes.PRICE;
	}

	function getAssetAddress() external view override returns (address) {
		return token;
	}

	function getChainId() external view override returns (uint32) {
		return chainId;
	}

}

contracts/DataAggregator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import { OApp, MessagingFee, Origin } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/OApp.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { LiquidityManager } from "./LiquidityManager.sol";
import { MessagingReceipt } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/OAppSender.sol";
import { IFlareContractRegistry } from "@flarenetwork/flare-periphery-contracts/coston2/util-contracts/userInterfaces/IFlareContractRegistry.sol";
import { IFastUpdater } from "@flarenetwork/flare-periphery-contracts/coston2/ftso/userInterfaces/IFastUpdater.sol";
import { IDataProvider, Data, DataTypes } from "./IDataProvider.sol";

uint32 constant CALLBACK_GAS_LIMIT = 4_000_000;

struct TokenInfo {
	string _name;
	address _address;
	uint32 _chainId;
	uint32 _id;
}

struct IndexUpdateMessage {
	Data[] liquidityMessages;
	Data[] supplyMessages;
	Data[] priceMessages;
}

struct AggregatorParams {
	uint256 _timeWindow;
	uint256 _sampleSize;
	uint256 _bribeUnit;
}

error NotEnoughBalance(uint256 currentBalance, uint256 calculatedFees);

contract DataAggregator is OApp {
	TokenInfo[] public tokenInfo;
	IDataProvider[] public dataProviders;
	TokenInfo[] tmpTokens;
	mapping(string => uint256) public tokens;
	string[] public tokenSymbols;

	uint256[] public totalSupplies;
	uint256[] public liquidities;
	uint256[] public prices;
	uint256[] public tokenParamsTimestampUpdates;

	Data[] public messages;

	mapping(uint256 => uint256[]) public movingAverage;
	uint256 sampleSize;
	uint256 timeWindow;
	uint256 samplingFrequency;
	uint256 lastSampleTime;
	uint256[] public lastIndexOrder;
	mapping(string => uint256[]) public tagsIndexOrder;
	mapping(string => uint256) public tagsIndexTimestamp;
	uint256 public lastIndexTimestamp;
	uint256 public bribeUnit;
	uint32 public chainId;
	uint32 public mainChainId;

	constructor(
		TokenInfo[] memory _tokenInfo,
		address _endpoint,
		address[] memory _dataProviders,
		AggregatorParams memory _aggregatorParams
	) OApp(_endpoint, msg.sender) {
		sampleSize = _aggregatorParams._sampleSize;
		timeWindow = _aggregatorParams._timeWindow;
		samplingFrequency = timeWindow / sampleSize;
		bribeUnit = _aggregatorParams._bribeUnit;

		for (uint256 i = 0; i < _dataProviders.length; i++) {
			dataProviders.push(IDataProvider(_dataProviders[i]));
		}

		for (uint256 i = 0; i < _tokenInfo.length; i++) {
			tokenInfo.push(_tokenInfo[i]);
			// tokenSymbols.push(_tokenInfo[i]._symbol);
			tokens[_tokenInfo[i]._name] = _tokenInfo[i]._id;
		}

		prices = new uint256[](tokenInfo.length);
		totalSupplies = new uint256[](tokenInfo.length);
		liquidities = new uint256[](tokenInfo.length);
		tokenParamsTimestampUpdates = new uint256[](tokenInfo.length);
	}

	function isMainChain() public view returns (bool) {
		return chainId == mainChainId;
	}

	function isOnSameChain(uint32 _chainId) public view returns (bool) {
		return chainId == _chainId;
	}

	function setChainId(uint32 _chainId, uint32 _mainChainId) external {
		chainId = _chainId;
		mainChainId = _mainChainId;
	}

	function updateTokenParams(
		uint256[] memory _totalSupplies,
		uint256[] memory _liquidities
	) external {
		for (uint256 i = 0; i < dataProviders.length; i++) {
			DataTypes dataType = dataProviders[i].getDataType();
			if (dataType == DataTypes.PRICE) {
				if (isOnSameChain(dataProviders[i].getChainId())) {
					prices[tokens[dataProviders[i].getLabel()]] = dataProviders[
						i
					].getMetricData();
				}
			}
			if (dataType == DataTypes.TOTAL_SUPPLY) {
				if (isOnSameChain(dataProviders[i].getChainId())) {
					totalSupplies[
						tokens[dataProviders[i].getLabel()]
					] = dataProviders[i].getMetricData();
				}
			}
			if (dataType == DataTypes.LIQUIDITY) {
				if (isOnSameChain(dataProviders[i].getChainId())) {
					liquidities[
						tokens[dataProviders[i].getLabel()]
					] = dataProviders[i].getMetricData();
				}
			}

			tokenParamsTimestampUpdates[
				tokens[dataProviders[i].getLabel()]
			] = dataProviders[i].getDataTimestamp();
		}

		// DATA MESSAGES
		if (isMainChain()) {
			for (uint256 i = 0; i < messages.length; i++) {
				if (messages[i].dataType == DataTypes.PRICE) {
					prices[tokens[messages[i].label]] = messages[i].metricData;
				}
				if (messages[i].dataType == DataTypes.TOTAL_SUPPLY) {
					totalSupplies[tokens[messages[i].label]] = messages[i]
						.metricData;
				}
				if (messages[i].dataType == DataTypes.LIQUIDITY) {
					liquidities[tokens[messages[i].label]] = messages[i]
						.metricData;
				}
				tokenParamsTimestampUpdates.push(messages[i].dataTimestamp);
			}
		}

		if (!isMainChain()) {
			Data[] memory _supplyMessages = new Data[](tokenInfo.length);
			Data[] memory _liquidityMessages = new Data[](tokenInfo.length);

			Data[] memory _priceMessages = new Data[](tokenInfo.length);

			for (uint256 i = 0; i < tokenInfo.length; i++) {
				if (chainId == tokenInfo[i]._chainId) {
					_supplyMessages[i] = Data(
						tokenInfo[i]._name,
						tokenInfo[i]._address,
						_totalSupplies[i],
						block.timestamp,
						DataTypes.TOTAL_SUPPLY,
						chainId
					);
					_liquidityMessages[i] = Data(
						tokenInfo[i]._name,
						tokenInfo[i]._address,
						_liquidities[i],
						block.timestamp,
						DataTypes.LIQUIDITY,
						chainId
					);
					_priceMessages[i] = Data(
						tokenInfo[i]._name,
						tokenInfo[i]._address,
						prices[i],
						block.timestamp,
						DataTypes.PRICE,
						chainId
					);
				}
			}
		}
	}

	function _lzReceive(
		Origin calldata /*_origin*/,
		bytes32 /*_guid*/,
		bytes calldata payload,
		address /*_executor*/,
		bytes calldata /*_extraData*/
	) internal override {
		// data = abi.decode(payload, (string));
		Data[] memory inboxMessages = abi.decode(payload, (Data[]));
		for (uint256 i = 0; i < inboxMessages.length; i++) {
			messages.push(inboxMessages[i]);
		}
	}

	function normalizePrice(
		uint256 price,
		int8 decimals
	) public pure returns (uint256) {
		int8 maxDecimals = 18; // Set maximum decimals to 10

		// Scale the price to the maximum number of decimals
		uint256 normalizedPrice = price *
			(10 ** uint256(uint8(maxDecimals - decimals)));

		return normalizedPrice;
	}

	function quote(
		uint32 _dstEid,
		string memory _message,
		bytes memory _options,
		bool _payInLzToken
	) public view returns (MessagingFee memory fee) {
		bytes memory payload = abi.encode(_message);
		fee = _quote(_dstEid, payload, _options, _payInLzToken);
	}

	function send(
		uint32 _dstEid,
		bytes memory _options,
		IndexUpdateMessage memory data
	) external payable returns (MessagingReceipt memory receipt) {
		bytes memory _payload = abi.encode(data);
		receipt = _lzSend(
			_dstEid,
			_payload,
			_options,
			MessagingFee(msg.value, 0),
			payable(msg.sender)
		);
	}
}

contracts/FtsoV2FeedConsumer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import { IFlareContractRegistry } from "@flarenetwork/flare-periphery-contracts/coston2/util-contracts/userInterfaces/IFlareContractRegistry.sol";
import { IFastUpdater } from "@flarenetwork/flare-periphery-contracts/coston2/ftso/userInterfaces/IFastUpdater.sol";
import { IFastUpdatesConfiguration } from "@flarenetwork/flare-periphery-contracts/coston2/ftso/userInterfaces/IFastUpdatesConfiguration.sol";

library FTSOFeedIdConverter {
	function FeedCategoryCrypto() internal pure returns (uint8) {
		return 1;
	}

	function getFeedId(
		uint8 _category,
		string memory _name
	) internal pure returns (bytes21) {
		bytes memory nameBytes = bytes(_name);
		require(nameBytes.length <= 20, "name too long");
		return bytes21(bytes.concat(bytes1(_category), nameBytes));
	}

	function getCryptoFeedId(
		string memory _name
	) internal pure returns (bytes21) {
		return
			bytes21(bytes.concat(bytes1(FeedCategoryCrypto()), bytes(_name)));
	}

	function getFeedCategoryAndName(
		bytes21 _feedId
	) internal pure returns (uint8 _category, string memory _name) {
		_category = uint8(_feedId[0]);
		uint256 length = 20;
		while (length > 0) {
			if (_feedId[length] != 0x00) {
				break;
			}
			length--;
		}
		bytes memory nameBytes = new bytes(length);
		for (uint256 i = 0; i < length; i++) {
			nameBytes[i] = _feedId[i + 1];
		}
		_name = string(nameBytes);
	}
}

contract FtsoV2FeedConsumer {
	IFlareContractRegistry internal contractRegistry;
	IFastUpdater internal ftsoV2;
    IFastUpdatesConfiguration internal fastUpdatesConfiguration;
	uint256[] public feedIndexes = [0, 2, 9];

	constructor() {
		contractRegistry = IFlareContractRegistry(
			0xaD67FE66660Fb8dFE9d6b1b4240d8650e30F6019
		);
		ftsoV2 = IFastUpdater(
			contractRegistry.getContractAddressByName("FastUpdater")
		);
	}



	/**
	 * Get the current value of the feeds.
	 */
	function getFtsoV2CurrentFeedValues()
		external
		view
		returns (
			uint256[] memory _feedValues,
			int8[] memory _decimals,
			uint64 _timestamp
		)
	{
		(
			uint256[] memory feedValues,
			int8[] memory decimals,
			uint64 timestamp
		) = ftsoV2.fetchCurrentFeeds(feedIndexes);
		/* Your custom feed consumption logic. In this example the values are just returned. */
		return (feedValues, decimals, timestamp);
	}

	function getFtsoV2CurrentFeedValuesByName(
		string[] memory _assetName
	)
		external
		view
		returns (
			uint256[] memory _feedValues,
			int8[] memory _decimals,
			uint64 _timestamp
		)
	{
		bytes21[] memory feedIds = new bytes21[](_assetName.length);
		for (uint256 i = 0; i < _assetName.length; i++) {
			feedIds[i] = FTSOFeedIdConverter.getCryptoFeedId(
				string.concat(string(_assetName[i]), string("/USD"))
			);
		}
		uint256[] memory indices = new uint256[](feedIds.length);
		for (uint256 i = 0; i < feedIds.length; i++) {
			indices[i] = fastUpdatesConfiguration.getFeedIndex(feedIds[i]);
		}
		(
			uint256[] memory feedValues,
			int8[] memory decimals,
			uint64 timestamp
		) = ftsoV2.fetchCurrentFeeds(indices);
		/* Your custom feed consumption logic. In this example the values are just returned. */
		return (feedValues, decimals, timestamp);
	}

	function getFtsoV2CurrentFeedValuesByName(
		string memory _assetName
	)
		external
		view
		returns (
			uint256[] memory _feedValues,
			int8[] memory _decimals,
			uint64 _timestamp
		)
	{
		bytes21[] memory feedIds = new bytes21[](1);

		feedIds[0] = FTSOFeedIdConverter.getCryptoFeedId(
			string.concat(string(_assetName), string("/USD"))
		);

		uint256[] memory indices = new uint256[](feedIds.length);
		for (uint256 i = 0; i < feedIds.length; i++) {
			indices[i] = fastUpdatesConfiguration.getFeedIndex(feedIds[i]);
		}
		(
			uint256[] memory feedValues,
			int8[] memory decimals,
			uint64 timestamp
		) = ftsoV2.fetchCurrentFeeds(indices);
		/* Your custom feed consumption logic. In this example the values are just returned. */
		return (feedValues, decimals, timestamp);
	}
}

