contracts/Uniswap/V4-Core/Claims.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;

import {Currency, CurrencyLibrary} from "./types/Currency.sol";
import {IClaims} from "./interfaces/IClaims.sol";

/// An intentionally barebones balance mapping only supporting mint/burn/transfer
contract Claims is IClaims {
    using CurrencyLibrary for Currency;

    // Mapping from Currency to account balances
    mapping(Currency currency => mapping(address account => uint256)) private balances;

    /// @inheritdoc IClaims
    function balanceOf(address account, Currency currency) public view returns (uint256) {
        return balances[currency][account];
    }

    /// @inheritdoc IClaims
    function transfer(address to, Currency currency, uint256 amount) public {
        if (to == address(this)) revert InvalidAddress();

        if (amount > balances[currency][msg.sender]) revert InsufficientBalance();
        unchecked {
            balances[currency][msg.sender] -= amount;
        }
        balances[currency][to] += amount;
        emit Transfer(msg.sender, to, currency, amount);
    }

    /// @notice Mint `amount` of currency to address
    /// @param to The address to mint to
    /// @param currency The currency to mint
    /// @param amount The amount to mint
    function _mint(address to, Currency currency, uint256 amount) internal {
        balances[currency][to] += amount;
        emit Mint(to, currency, amount);
    }

    /// @notice Burn `amount` of currency from msg.sender
    /// @param currency The currency to mint
    /// @param amount The amount to burn
    /// @dev Will revert if the sender does not have enough balance
    function _burn(Currency currency, uint256 amount) internal {
        if (amount > balances[currency][msg.sender]) revert InsufficientBalance();
        unchecked {
            balances[currency][msg.sender] -= amount;
        }
        emit Burn(msg.sender, currency, amount);
    }
}

contracts/Uniswap/V4-Core/Fees.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity ^0.8.19;

import {Currency, CurrencyLibrary} from "./types/Currency.sol";
import {IProtocolFeeController} from "./interfaces/IProtocolFeeController.sol";
import {IHookFeeManager} from "./interfaces/IHookFeeManager.sol";
import {IFees} from "./interfaces/IFees.sol";
import {FeeLibrary} from "./libraries/FeeLibrary.sol";
import {Pool} from "./libraries/Pool.sol";
import {PoolKey} from "./types/PoolKey.sol";
import {Owned} from "./Owned.sol";
import {IDynamicFeeManager} from "./interfaces/IDynamicFeeManager.sol";
import "hardhat/console.sol";

abstract contract Fees is IFees, Owned {
    using FeeLibrary for uint24;
    using CurrencyLibrary for Currency;

    uint8 public constant MIN_PROTOCOL_FEE_DENOMINATOR = 4;

    // the swap fee is represented in hundredths of a bip, so the max is 100%
    uint24 public constant MAX_SWAP_FEE = 1000000;

    mapping(Currency currency => uint256) public protocolFeesAccrued;

    mapping(address hookAddress => mapping(Currency currency => uint256))
        public hookFeesAccrued;

    IProtocolFeeController public protocolFeeController;

    uint256 private immutable controllerGasLimit;

    constructor(uint256 _controllerGasLimit) {
        controllerGasLimit = _controllerGasLimit;
    }

    function _fetchProtocolFees(
        PoolKey memory key
    ) internal view returns (uint24 protocolFees) {
        uint16 protocolSwapFee;
        uint16 protocolWithdrawFee;
        if (address(protocolFeeController) != address(0)) {
            // note that EIP-150 mandates that calls requesting more than 63/64ths of remaining gas
            // will be allotted no more than this amount, so controllerGasLimit must be set with this
            // in mind.
            if (gasleft() < controllerGasLimit)
                revert ProtocolFeeCannotBeFetched();
            try
                protocolFeeController.protocolFeesForPool{
                    gas: controllerGasLimit
                }(key)
            returns (uint24 updatedProtocolFees) {
                protocolSwapFee = uint16(updatedProtocolFees >> 12);
                protocolWithdrawFee = uint16(updatedProtocolFees & 0xFFF);

                protocolFees = updatedProtocolFees;
            } catch {}
            _checkProtocolFee(protocolSwapFee);
            _checkProtocolFee(protocolWithdrawFee);
        }
    }

    /// @notice There is no cap on the hook fee, but it is specified as a percentage taken on the amount after the protocol fee is applied, if there is a protocol fee.
    function _fetchHookFees(
        PoolKey memory key
    ) internal view returns (uint24 hookFees) {
        if (address(key.hooks) != address(0)) {
            try IHookFeeManager(address(key.hooks)).getHookFees(key) returns (
                uint24 hookFeesRaw
            ) {
                uint24 swapFeeMask = key.fee.hasHookSwapFee() ? 0xFFF000 : 0;
                uint24 withdrawFeeMask = key.fee.hasHookWithdrawFee()
                    ? 0xFFF
                    : 0;
                uint24 fullFeeMask = swapFeeMask | withdrawFeeMask;
                hookFees = hookFeesRaw & fullFeeMask;
            } catch {}
        }
    }

    function _fetchDynamicSwapFee(
        PoolKey memory key
    ) internal view returns (uint24 dynamicSwapFee) {
        dynamicSwapFee = IDynamicFeeManager(address(key.hooks)).getFee(
            msg.sender,
            key
        );
        if (dynamicSwapFee >= MAX_SWAP_FEE) revert FeeTooLarge();
    }

    /// @dev Only the lower 12 bits are used here to encode the fee denominator.
    function _checkProtocolFee(uint16 fee) internal pure {
        if (fee != 0) {
            uint16 fee0 = fee % 64;
            uint16 fee1 = fee >> 6;
            // The fee is specified as a denominator so it cannot be LESS than the MIN_PROTOCOL_FEE_DENOMINATOR (unless it is 0).
            if (
                (fee0 != 0 && fee0 < MIN_PROTOCOL_FEE_DENOMINATOR) ||
                (fee1 != 0 && fee1 < MIN_PROTOCOL_FEE_DENOMINATOR)
            ) {
                revert FeeTooLarge();
            }
        }
    }

    function setProtocolFeeController(
        IProtocolFeeController controller
    ) external onlyOwner {
        protocolFeeController = controller;
        emit ProtocolFeeControllerUpdated(address(controller));
    }

    function collectProtocolFees(
        address recipient,
        Currency currency,
        uint256 amount
    ) external returns (uint256 amountCollected) {
        if (msg.sender != owner && msg.sender != address(protocolFeeController))
            revert InvalidCaller();

        amountCollected = (amount == 0)
            ? protocolFeesAccrued[currency]
            : amount;
        protocolFeesAccrued[currency] -= amountCollected;
        currency.transfer(recipient, amountCollected);
    }

    function collectHookFees(
        address recipient,
        Currency currency,
        uint256 amount
    ) external returns (uint256 amountCollected) {
        address hookAddress = msg.sender;

        amountCollected = (amount == 0)
            ? hookFeesAccrued[hookAddress][currency]
            : amount;
        recipient = (recipient == address(0)) ? hookAddress : recipient;

        hookFeesAccrued[hookAddress][currency] -= amountCollected;
        currency.transfer(recipient, amountCollected);
    }
}

